mapper.select:
select 

from t_company
where id = #{id,jdbcType=INTEGER}

select * from v_cosmetologist a where 1=1

  and a.companyid=#{record.id}

ORDER BY a.sort asc,a.id asc

select * from v_counsoler a where 1=1

  and a.companyid=#{record.id}

ORDER BY a.sort asc,a.id asc

select * from v_project_inventory a where 1=1

  and a.companyid=#{record.id}


  and (a.name like concat('%',#{record.q},'%') or a.spell like concat('%',#{record.q},'%'))


select * from ${record.viewname} a where 1=1


  and a.companyid=${record.id}


  and a.pid=${record.pid}


  order by ${record.sortName}

SELECT temp.datetime,temp.nickname,sum(temp.c_total_amount)c_total_amount,sum(temp.f_total_amount)f_total_amount,sum(temp.total_amont)total_amont,sum(temp.CHUZHEN)CHUZHEN,sum(temp.FUZHEN)FUZHEN,sum(temp.employeeWastageOrder)employeeWastageOrder,sum(temp.employeeTotalOrder)employeeTotalOrder from ( SELECT
DATE_FORMAT(a.createTime, '%Y') datetime,
DATE_FORMAT(a.createTime, '%Y%m') datetime,
DATE_FORMAT(a.createTime, '%Y%m%d') datetime,
DATE_FORMAT(a.createTime, '%Y%m%d') datetime,
(sum(IF (a.`status` = 1,IF (a.chu_fu_zhen = 0,ifnull(b.total_amount, 0),0),0)) / 10000) c_total_amount, (sum(IF (a.`status` = 1,IF (a.chu_fu_zhen = 1,ifnull(b.total_amount, 0),0),0)) / 10000) f_total_amount, sum(ifnull(b.total_amount, 0)) / 10000 AS total_amont, sum(IF (a.`status` = 1,IF (a.chu_fu_zhen = 0, 1, 0),0)) CHUZHEN, sum(IF (a.`status` = 1,IF (a.chu_fu_zhen = 1, 1, 0),0)) FUZHEN, sum(IF (a.`status` = 99,1,0)) employeeWastageOrder, count(a.id) employeeTotalOrder, tsu.nickname FROM t_ltn_customer a LEFT JOIN (select sum(b.total_amount)total_amount,b.customer_id as id from t_customer_consumpton_amount b LEFT JOIN t_ltn_customer b1 ON b.customer_id = b1.id WHERE b1.`status` = 1 GROUP BY b.customer_id) b on a.id = b.id INNER JOIN t_sys_user tsu on a.counsoler = tsu.id and a.company_id = tsu.companyid and tsu.isvalid=1 INNER JOIN t_customer_porject tcp on a.id = tcp.order_id and tcp.update_time is null and DATE_FORMAT(a.createTime,'%Y-%m-%d')=DATE_FORMAT(tcp.create_time, '%Y-%m-%d') where 1 = 1
and DATE_FORMAT(a.createTime, '%Y-%m-%d') >= #{starttime}
and DATE_FORMAT(a.createTime, '%Y-%m-%d') <= #{endtime}
and a.company_id = #{companyid}
GROUP BY datetime,a.counsoler,tsu.nickname
union all SELECT
DATE_FORMAT(tcpl.create_time, '%Y') datetime,
DATE_FORMAT(tcpl.create_time, '%Y%m') datetime,
DATE_FORMAT(tcpl.create_time, '%Y%m%d') datetime,
DATE_FORMAT(tcpl.create_time, '%Y%m%d') datetime,
0 as c_total_amount, 0 as f_total_amount, 0 as total_amont, 0 as CHUZHEN, count(tcpl.id) FUZHEN, 0 as employeeWastageOrder, count(tcpl.id) employeeTotalOrder, tsu.nickname from t_ltn_customer a INNER JOIN t_customer_porject tcp on a.id = tcp.order_id and DATE_FORMAT(a.createTime,'%Y-%m-%d')<DATE_FORMAT(tcp.update_time, '%Y-%m-%d') INNER JOIN t_customer_project_log tcpl on tcp.id=tcpl.cp_id INNER JOIN t_company tc ON a.company_id = tc.id and tc.`status`=1 INNER JOIN t_sys_user tsu on a.counsoler = tsu.id and a.company_id = tsu.companyid and tsu.isvalid=1 where a.`status`=1
and DATE_FORMAT(tcpl.create_time, '%Y-%m-%d') >= #{starttime}
and DATE_FORMAT(tcpl.create_time, '%Y-%m-%d') <= #{endtime}
and a.company_id = #{companyid}
GROUP BY datetime,tsu.nickname ) temp GROUP BY temp.datetime,temp.nickname order by datetime desc,total_amont desc

SELECT temp1.datetime, tc.name as companyname, MAX(CASE vpm.value WHEN 1 THEN temp1.pronumbs ELSE 0 END ) pn1, MAX(CASE vpm.value WHEN 2 THEN temp1.pronumbs ELSE 0 END ) pn2, MAX(CASE vpm.value WHEN 3 THEN temp1.pronumbs ELSE 0 END ) pn3, MAX(CASE vpm.value WHEN 4 THEN temp1.pronumbs ELSE 0 END ) pn4, MAX(CASE vpm.value WHEN 5 THEN temp1.pronumbs ELSE 0 END ) pn5, MAX(CASE vpm.value WHEN 6 THEN temp1.pronumbs ELSE 0 END ) pn6, MAX(CASE vpm.value WHEN 7 THEN temp1.pronumbs ELSE 0 END ) pn7, MAX(CASE vpm.value WHEN 8 THEN temp1.pronumbs ELSE 0 END ) pn8 from v_project_model vpm LEFT JOIN ( SELECT
DATE_FORMAT(c.createTime, '%Y') datetime,
DATE_FORMAT(c.createTime, '%Y%m') datetime,
DATE_FORMAT(c.createTime, '%Y%m%d') datetime,
a.project_model, count(b.id) pronumbs, c.company_id FROM t_project a LEFT JOIN v_project_model pm on a.project_model=pm.`value` and a.company_id=pm.companyid INNER JOIN t_customer_consumpton_amount b ON a.id = b.project_id INNER JOIN t_ltn_customer c on b.customer_id=c.id and a.company_id= c.company_id where 1=1
and c.company_id=#{companyid}
and c.`status`=1
and DATE_FORMAT(c.createTime, '%Y-%m-%d') >= #{starttime}
and DATE_FORMAT(c.createTime, '%Y-%m-%d') <= #{endtime}
GROUP BY DATE_FORMAT(c.createTime, '%Y'),b.project_id,c.company_id,a.project_model
GROUP BY DATE_FORMAT(c.createTime, '%Y%m'),b.project_id,c.company_id,a.project_model
GROUP BY DATE_FORMAT(c.createTime, '%Y%m%d'),b.project_id,c.company_id,a.project_model
) temp1 on vpm.companyid = temp1.company_id and vpm.`value`=temp1.project_model INNER JOIN t_company tc on vpm.companyid=tc.id and temp1.company_id= tc.id and tc.`status`=1 where 1=1
and vpm.companyid=#{companyid}
GROUP BY tc.name,temp1.datetime desc ORDER BY tc.id

SELECT temp1.datetime, tc.name as companyname, MAX(CASE vpm.value WHEN 1 THEN temp1.proamount ELSE 0 END ) pn1, MAX(CASE vpm.value WHEN 2 THEN temp1.proamount ELSE 0 END ) pn2, MAX(CASE vpm.value WHEN 3 THEN temp1.proamount ELSE 0 END ) pn3, MAX(CASE vpm.value WHEN 4 THEN temp1.proamount ELSE 0 END ) pn4, MAX(CASE vpm.value WHEN 5 THEN temp1.proamount ELSE 0 END ) pn5, MAX(CASE vpm.value WHEN 6 THEN temp1.proamount ELSE 0 END ) pn6, MAX(CASE vpm.value WHEN 7 THEN temp1.proamount ELSE 0 END ) pn7, MAX(CASE vpm.value WHEN 8 THEN temp1.proamount ELSE 0 END ) pn8 from v_project_model vpm LEFT JOIN ( SELECT
DATE_FORMAT(c.createTime, '%Y') datetime,
DATE_FORMAT(c.createTime, '%Y%m') datetime,
DATE_FORMAT(c.createTime, '%Y%m%d') datetime,
a.project_model, sum(b.total_amount) proamount, c.company_id FROM t_project a LEFT JOIN v_project_model pm on a.project_model=pm.`value` and a.company_id=pm.companyid INNER JOIN t_customer_consumpton_amount b ON a.id = b.project_id INNER JOIN t_ltn_customer c on b.customer_id=c.id and a.company_id= c.company_id where 1=1
and c.company_id=#{companyid}
and c.`status`=1
and DATE_FORMAT(c.createTime, '%Y-%m-%d') >= #{starttime}
and DATE_FORMAT(c.createTime, '%Y-%m-%d') <= #{endtime}
GROUP BY DATE_FORMAT(c.createTime, '%Y'),b.project_id,c.company_id,a.project_model
GROUP BY DATE_FORMAT(c.createTime, '%Y%m'),b.project_id,c.company_id,a.project_model
GROUP BY DATE_FORMAT(c.createTime, '%Y%m%d'),b.project_id,c.company_id,a.project_model
) temp1 on vpm.companyid = temp1.company_id and vpm.`value`=temp1.project_model INNER JOIN t_company tc on vpm.companyid=tc.id and temp1.company_id= tc.id and tc.`status`=1 where 1=1
and vpm.companyid=#{companyid}
GROUP BY tc.name,temp1.datetime desc ORDER BY tc.id

select * from ( SELECT tsr.role_name,tsr.role_sign,'counsoler' as rtype,temp.nickname,temp.image_path,sum(temp.c_total_amount)c_total_amount,sum(temp.f_total_amount)f_total_amount,sum(temp.total_amont)total_amont,sum(temp.CHUZHEN)CHUZHEN,sum(temp.FUZHEN)FUZHEN,sum(temp.employeeWastageOrder)employeeWastageOrder,sum(temp.employeeTotalOrder)employeeTotalOrder from ( SELECT (sum(IF (a.`status` = 1,IF (a.chu_fu_zhen = 0,ifnull(b.total_amount, 0),0),0))) c_total_amount, (sum(IF (a.`status` = 1,IF (a.chu_fu_zhen = 1,ifnull(b.total_amount, 0),0),0))) f_total_amount, sum(ifnull(b.total_amount, 0)) AS total_amont, sum(IF (a.`status` = 1,IF (a.chu_fu_zhen = 0, 1, 0),0)) CHUZHEN, sum(IF (a.`status` = 1,IF (a.chu_fu_zhen = 1, 1, 0),0)) FUZHEN, sum(IF (a.`status` = 99,1,0)) employeeWastageOrder, count(a.id) employeeTotalOrder, tsu.nickname, tsue.image_path FROM t_ltn_customer a LEFT JOIN (select sum(b.total_amount)total_amount,b.customer_id as id from t_customer_consumpton_amount b LEFT JOIN t_ltn_customer b1 ON b.customer_id = b1.id WHERE b1.`status` = 1 GROUP BY b.customer_id) b on a.id = b.id INNER JOIN t_sys_user tsu on a.counsoler = tsu.id and a.company_id = tsu.companyid and tsu.isvalid=1 LEFT JOIN t_sys_user_ext tsue on tsu.id = tsue.user_id where 1 = 1 and DATE_FORMAT(a.createTime, '%Y-%m') >= DATE_FORMAT(now(),'%Y-%m')
and a.company_id = #{companyid}
GROUP BY a.counsoler,tsu.nickname,tsue.image_path union all SELECT 0 as c_total_amount, 0 as f_total_amount, 0 as total_amont, 0 as CHUZHEN, count(tcpl.id) FUZHEN, 0 as employeeWastageOrder, count(tcpl.id) employeeTotalOrder, tsu.nickname,tsue.image_path from t_ltn_customer a INNER JOIN t_customer_porject tcp on a.id = tcp.order_id and DATE_FORMAT(a.createTime,'%Y-%m-%d')<DATE_FORMAT(tcp.create_time, '%Y-%m-%d') INNER JOIN t_customer_project_log tcpl on tcp.id=tcpl.cp_id INNER JOIN t_company tc ON a.company_id = tc.id and tc.`status`=1 INNER JOIN t_sys_user tsu on a.counsoler = tsu.id and a.company_id = tsu.companyid and tsu.isvalid=1 LEFT JOIN t_sys_user_ext tsue on tsu.id = tsue.user_id where a.`status`=1 and DATE_FORMAT(a.createTime, '%Y-%m') >= DATE_FORMAT(now(),'%Y-%m')
and a.company_id = #{companyid}
GROUP BY tsu.nickname,tsue.image_path ) temp LEFT JOIN t_sys_user tsu1 on temp.nickname=tsu1.nickname LEFT JOIN t_sys_user_role tsur on tsu1.id=tsur.user_id LEFT JOIN t_sys_role tsr on tsur.role_id = tsr.id LEFT JOIN t_sys_user_ext tsue on tsur.user_id = tsue.user_id GROUP BY temp.nickname,tsr.role_name,tsr.role_sign,temp.image_path union ALL SELECT tsr.role_name,tsr.role_sign,'therapeutist' as rtype,tsu.nickname,tsue.image_path, (sum(IF (a.`status` = 1,IF (a.chu_fu_zhen = 0,ifnull(b.total_amount, 0),0),0))) c_total_amount, (sum(IF (a.`status` = 1,IF (a.chu_fu_zhen = 1,ifnull(b.total_amount, 0),0),0))) f_total_amount, sum(ifnull(b.total_amount, 0)) AS total_amont, sum(IF (a.`status` = 1,IF (a.chu_fu_zhen = 0, 1, 0),0)) CHUZHEN, sum(IF (a.`status` = 1,IF (a.chu_fu_zhen = 1, 1, 0),0)) FUZHEN, sum(IF (a.`status` = 99,1,0)) employeeWastageOrder, count(a.id) employeeTotalOrder FROM t_ltn_customer a LEFT JOIN (select sum(b.total_amount)total_amount,b.customer_id as id from t_customer_consumpton_amount b LEFT JOIN t_ltn_customer b1 ON b.customer_id = b1.id WHERE b1.`status` = 1 GROUP BY b.customer_id) b on a.id = b.id INNER JOIN t_sys_user tsu on a.therapeutist = tsu.id and a.company_id = tsu.companyid and tsu.isvalid=1 LEFT JOIN t_sys_user tsu1 on a.therapeutist=tsu1.id LEFT JOIN t_sys_user_role tsur on tsu1.id=tsur.user_id LEFT JOIN t_sys_role tsr on tsur.role_id = tsr.id LEFT JOIN t_sys_user_ext tsue on tsur.user_id = tsue.user_id where 1 = 1 and DATE_FORMAT(a.createTime, '%Y-%m') >= DATE_FORMAT(now(),'%Y-%m')
and a.company_id = #{companyid}
GROUP BY a.therapeutist,tsu.nickname,tsr.role_name,tsr.role_sign ORDER BY employeeTotalOrder desc,c_total_amount desc limit 7 ) ttemp LEFT JOIN t_sys_role tsr1 on ttemp.role_sign = tsr1.role_sign ORDER BY tsr1.sort

SELECT vpm.`name`, count(tpcca.orderid) procounts FROM t_project tp LEFT JOIN ( SELECT DISTINCT tcca.project_id, tc.id as orderid, tc.company_id FROM t_ltn_customer tc LEFT JOIN t_customer_consumpton_amount tcca ON tc.id = tcca.customer_id WHERE 1=1
and tc.company_id = #{companyid}
AND tc.createTime BETWEEN #{starttime}
AND #{endtime}
AND now()
GROUP BY tcca.project_id,tc.company_id,tc.id ) tpcca ON tp.id = tpcca.project_id and tpcca.company_id=tp.company_id LEFT JOIN v_project_model vpm ON tp.project_model = vpm.`value` AND tp.company_id = vpm.companyid INNER JOIN t_company tcp on tp.company_id=tcp.id and tcp.`status`=1 WHERE tp.`status` = 1
and tp.company_id = #{companyid}
GROUP BY vpm.name,tp.company_id ORDER BY procounts DESC

SELECT sum(if(temp3.project_model=1,1,0)) as p1, sum(if(temp3.project_model=2,1,0)) as p2, sum(if(temp3.project_model=3,1,0)) as p3, sum(if(temp3.project_model=5,1,0)) as p4, sum(if(temp3.project_model=6,1,0)) as p5, sum(if(temp3.project_model=7,1,0)) as p6, sum(if(temp3.project_model=8,1,0)) as p7 from ( SELECT DISTINCT tcca.project_id, tc.id as orderid, tc.phonenumb, tc.company_id, 4 as project_model FROM t_ltn_customer tc INNER JOIN t_customer_consumpton_amount tcca ON tc.id = tcca.customer_id INNER JOIN t_project tp on tcca.project_id = tp.id and tp.project_model=4 WHERE 1=1
and tc.company_id = #{companyid}
AND tc.createTime BETWEEN #{starttime}
AND #{endtime}
AND now()
GROUP BY tcca.project_id,tc.company_id,tc.id,tc.phonenumb ) temp2 INNER JOIN ( SELECT temp1.*,tcca.project_id,tp.project_model from ( SELECT tlc.id,tlc.phonenumb,tlc.customername,tlc.createTime,tlc.company_id from t_ltn_customer tlc INNER JOIN ( SELECT DISTINCT tcca.project_id, tc.id as orderid, tc.phonenumb, tc.company_id, tc.createTime, 4 as projecttype FROM t_ltn_customer tc INNER JOIN t_customer_consumpton_amount tcca ON tc.id = tcca.customer_id INNER JOIN t_project tp on tcca.project_id = tp.id and tp.project_model=4 WHERE 1=1
and tc.company_id = #{companyid}
AND tc.createTime BETWEEN #{starttime}
AND #{endtime}
AND now()
GROUP BY tcca.project_id,tc.company_id,tc.id,tc.phonenumb ) temp on temp.phonenumb=tlc.phonenumb and temp.company_id=tlc.company_id and tlc.createTime < temp.createTime and tlc.id<>temp.orderid where 1=1
and tlc.createTime > #{starttime}
) temp1 INNER JOIN t_customer_consumpton_amount tcca ON temp1.id = tcca.customer_id INNER JOIN t_project tp on tcca.project_id = tp.id GROUP BY temp1.phonenumb,temp1.company_id,temp1.id,tcca.project_id )temp3 on temp2.company_id=temp3.company_id and temp2.phonenumb=temp3.phonenumb and temp2.project_model<>temp3.project_model GROUP BY temp2.company_id
