mapper.select:
select 

from t_company
where id = #{id,jdbcType=INTEGER}


select * from ${record.viewname} a where 1=1


  and a.companyid=${record.id}


  and a.pid=${record.pid}

SELECT p.id,p.`code`,p.`name`,p.remark,p.`status`,p.province_id as provinceId,p.city_id as cityId,p.county_id as countyId,p.createTime,p.createUser,p.updateTime,p.updateUser, CONCAT(tp.`name`,tc.`name`,tcy.`name`,ifnull(p.detail_address,'')) as address,p.detail_address as detailAddress FROM `t_company` p LEFT JOIN t_province tp ON p.province_id = tp.id LEFT JOIN t_city tc ON p.city_id = tc.id LEFT JOIN t_county tcy ON p.county_id = tcy.id where 1=1
and p.name like concat('%',#{name},'%')
and p.status=#{status}
order by p.updateTime desc
limit #{index},#{pageSize}

select count(*) from( SELECT p.id,p.`code`,p.`name`,p.remark,p.`status`,p.province_id as provinceId,p.city_id as cityId,p.county_id as countyId,p.createTime,p.createUser,p.updateTime,p.updateUser, CONCAT(tp.`name`,tc.`name`,tcy.`name`,ifnull(p.detail_address,'')) as address,p.detail_address as detailAddress FROM `t_company` p LEFT JOIN t_province tp ON p.province_id = tp.id LEFT JOIN t_city tc ON p.city_id = tc.id LEFT JOIN t_county tcy ON p.county_id = tcy.id where 1=1
and p.name like concat('%',#{name},'%')
and p.status=#{status}
) temp
