mapper.insert:
insert into t_company (id, code, name, 
  province_id, city_id, county_id, 
  status, remark, createTime, 
  updateTime, createUser, updateUser
  )
values (#{id,jdbcType=INTEGER}, #{code,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{provinceId,jdbcType=INTEGER}, #{cityId,jdbcType=INTEGER}, #{countyId,jdbcType=INTEGER}, 
  #{status,jdbcType=BIT}, #{remark,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}, 
  #{updatetime,jdbcType=TIMESTAMP}, #{createuser,jdbcType=VARCHAR}, #{updateuser,jdbcType=VARCHAR}
  )

mapper.select:

select * from ${record.viewname} a where 1=1


  and a.companyid=${record.id}


  and a.pid=${record.pid}
