mapper.select:
SELECT
	p.id,p.consumpton_amount_id as consumptonAmountId,p.numbs,ti.`code`,ti.`name`,ti.factory,ti.inventory,ti.price,ti.`status`,ti.units,p.createUser,p.createTime,ti.name as projectName,p.updateTime,p.updateUser
FROM
	`t_consumpton_project` p
LEFT JOIN t_inventory ti on p.project_id=ti.id
where 1=1  

	and p.consumpton_amount_id =#{consumptonAmountId}


	and p.project_id=#{projectId}

order by p.updateTime desc 

	limit #{index},#{pageSize}

select count(*) from (
 	SELECT
		p.id,p.consumpton_amount_id as consumptonAmountId,p.numbs,ti.`code`,ti.`name`,ti.factory,ti.inventory,ti.price,ti.`status`,ti.units,p.createUser,p.createTime,ti.name as projectName,p.updateTime,p.updateUser
	FROM
		`t_consumpton_project` p
	LEFT JOIN t_inventory ti on p.project_id=ti.id
	where 1=1  
	
		and p.consumpton_amount_id =#{consumptonAmountId}
	
	
		and p.project_id=#{projectId}
	
) temp
