mapper.select:
SELECT p.id,p.company_id as companyId,p.create_time as createTime,p.create_user as createUser,p.customer_name customerName,p.customer_phone as customerPhone,p.project,p.remark,p.root_in as rootIn, p.sex as sex,DATE_FORMAT(p.subscribe_date,'%Y-%m-%d %H:%i:%S') as subscribeDate,p.update_time as updateTime,p.update_user as updateUser,p.status,vsb.text statusName, tc.`name` as companyName,vri.`name` as rootInName,p.email,p.profession,vcp.text as professionName FROM t_customer_subscribe p LEFT JOIN v_customer_profession vcp on p.profession=vcp.value INNER JOIN t_company tc on p.company_id=tc.id and tc.`status`=1 LEFT JOIN v_root_in vri on p.root_in=vri.`value`
and vri.companyid=#{companyid}
LEFT JOIN v_subscribe vsb on p.status=vsb.`value`
and vsb.companyid=#{companyid}
where 1=1
and p.customer_name like concat('%',#{customername},'%')
and p.customer_phone like concat('%',#{customerphone},'%')
and p.update_time >= #{updatetime}
and p.remark like concat('%',#{remark},'%')
and p.company_id=#{companyid}
and p.status=#{status}
order by p.update_time desc
limit #{index},#{pageSize}

select count(*) from (
	SELECT
		p.id
	FROM
		t_customer_subscribe p
	INNER JOIN t_company tc on p.company_id=tc.id and tc.`status`=1
	LEFT JOIN v_root_in vri on p.root_in=vri.`value`
	
	 and vri.companyid=#{companyid}
	
	LEFT JOIN v_subscribe vsb on p.status=vsb.`value`
	
	 and vsb.companyid=#{companyid}
	 
	where 1=1 
	
		and p.customer_name like concat('%',#{customername},'%')
	
	
		and p.customer_phone like concat('%',#{customerphone},'%')
	
	
		and p.update_time >= #{updatetime}
	
	
		and p.remark like concat('%',#{remark},'%')
	
	
		and p.company_id=#{companyid}
	
	
		and p.status=#{status}
	
) temp
