mapper.select:
SELECT p.id,p.company_id as companyId,p.name,p.code,p.buy_date as buyDate,p.buy_user as buyUser,p.buy_price as buyPrice,p.depreciation_life as depreciationLife ,p.factory,p.manager,p.`status`,p.type,p.create_time,p.update_time,tc.`name` as companyName,u1.nickname as buyUserName,u2.nickname as managerName,u3.nickname as createUser,u4.nickname as updateUser ,ves.text as statusName,vet.text as typeName FROM `t_equipment` p LEFT JOIN t_company tc on p.company_id=tc.id LEFT JOIN t_sys_user u1 on p.buy_user =u1.id and u1.companyid=p.company_id LEFT JOIN t_sys_user u2 on p.manager =u2.id and u2.companyid=p.company_id LEFT JOIN t_sys_user u3 on p.create_user =u3.username and u3.companyid=p.company_id LEFT JOIN t_sys_user u4 on p.update_user =u4.username and u4.companyid=p.company_id LEFT JOIN v_equipment_status ves on p.`status`=ves.`value` LEFT JOIN v_equipment_type vet on p.type =vet.`value` where 1=1
and p.name like concat('%',#{name},'%')
and p.status=#{status}
and p.type=#{type}
and p.company_id=#{companyid}
order by p.update_time desc
limit #{index},#{pageSize}

select count(*) from( SELECT p.id,p.company_id as companyId,p.name,p.code,p.buy_date as buyDate,p.buy_user as buyUser,p.buy_price as buyPrice,p.depreciation_life as depreciationLife ,p.factory,p.manager,p.`status`,p.type,p.create_time,p.update_time,tc.`name` as companyName,u1.nickname as buyUserName,u2.nickname as managerName,u3.nickname as createUser,u4.nickname as updateUser ,ves.text as statusName,vet.text as typeName FROM `t_equipment` p LEFT JOIN t_company tc on p.company_id=tc.id LEFT JOIN t_sys_user u1 on p.buy_user =u1.username and u1.companyid=p.company_id LEFT JOIN t_sys_user u2 on p.manager =u2.username and u2.companyid=p.company_id LEFT JOIN t_sys_user u3 on p.create_user =u3.username and u3.companyid=p.company_id LEFT JOIN t_sys_user u4 on p.update_user =u4.username and u4.companyid=p.company_id LEFT JOIN v_equipment_status ves on p.`status`=ves.`value` LEFT JOIN v_equipment_type vet on p.type =vet.`value` where 1=1
and p.name like concat('%',#{name},'%')
and p.status=#{status}
and p.type=#{type}
and p.company_id=#{companyid}
) temp

SELECT
	sum(ifnull(p.buy_price, 0)) total_amount,
	sum(if(p.`status`>0 and p.`status`<5,ifnull(p.buy_price, 0),0)) actual_total_amount
FROM
	`t_equipment` p
where 1=1 

	and p.name like concat('%',#{name},'%')


	and p.status=#{status}


	and p.type=#{type}


	and p.company_id=#{companyid}
