mapper.select:
SELECT a.id, a.company_id as companyId, c.name as companyName, ifnull(a.chu_fu_zhen,0) as chuFuZhen, a.phonenumb, a.status, a.type, a.therapeutist, a.customername, DATE_FORMAT(a.remindtime,'%Y-%m-%d')remindtime, a.createUser, a.updateUser, a.createTime, a.updateTime, a.remark, ifnull((select sum(ifnull(cc.total_amount,0)) from t_ltn_customer_consumpton_amount cc where cc.customer_id= a.id),0) as totalConsumptonAmount, u.nickname as therapeutistName, vct.text as typeName, a.sex, vs.text as sexName, a.card_id as cardId, a.counsoler, u1.nickname as counsolerName, u2.nickname as subscribeName, vcos.text as statusName from t_ltn_customer a LEFT JOIN t_customer_subscribe tcs on a.subscribe_id=tcs.id
and tcs.company_id=#{record.companyId}
LEFT JOIN t_sys_user u2 on tcs.create_user=u2.username
and u2.companyid=#{record.companyId}
left join v_consume_type vct on a.type=vct.value
and vct.companyid=#{record.companyId}
LEFT JOIN t_sys_user u on a.therapeutist = u.id
and u.companyid=#{record.companyId}
LEFT JOIN t_sys_user u1 on a.counsoler = u1.id
and u1.companyid=#{record.companyId}
left join t_company c on a.company_id = c.id left join v_sex vs on a.sex=vs.value
and vs.companyid=#{record.companyId}
LEFT JOIN v_customer_order_status vcos on a.`status`= vcos.`value` where 1=1
and a.company_id=#{record.companyId}
and a.phonenumb like concat(#{record.phonenumb},'%')
and a.customername like concat(#{record.customername},'%')
and a.therapeutist = #{record.therapeutist}
and a.chu_fu_zhen = #{record.chuFuZhen}
and a.status in
#{item}
and a.type = #{record.type}
and a.id = #{record.id}
and a.createtime >= #{record.createtime}
and a.createtime <= #{record.endtime}
GROUP BY a.id,a.company_id,vct.text,vs.text,u2.id,vcos.text order by a.updateTime desc
limit #{record.page},#{record.rows}

  		 SELECT
			lc.customername as '客户姓名',
			lc.phonenumb '客户电话',
			vcos.text as '结算状态',
			ifnull(vct.text,'') as '结算类型',
			ifnull(u2.nickname,'') '预约人',
			ifnull(u1.nickname,'') '咨询师',
			ifnull(u.nickname,'') '治疗师',
			ifnull(tcdt.projectnames,'') '就诊项目',
			ifnull((select sum(ifnull(cc.total_amount,0)) from t_ltn_customer_consumpton_amount cc where cc.customer_id= lc.id),0) '消费总金额',
			ifnull(lc.remindtime,'') '回访时间',
			ifnull(u3.nickname,'') '创建人',
			DATE_FORMAT(lc.createTime,'%Y-%m-%d %H:%i:%S') as '创建时间',
			ifnull(lc.remark,'') as '说明'
		from t_ltn_customer lc 
		LEFT JOIN t_customer_subscribe tcs on lc.subscribe_id=tcs.id
		
		 and tcs.company_id=#{record.companyId}
		
		LEFT JOIN t_sys_user u3 on lc.createUser=u3.username
		
		 and u3.companyid=#{record.companyId}
		
		LEFT JOIN t_sys_user u2 on tcs.create_user=u2.username
		
		 and u2.companyid=#{record.companyId}
		
		LEFT JOIN t_sys_user u on lc.therapeutist = u.id
		
		 and u.companyid=#{record.companyId}
		
		left join v_consume_type vct on lc.type=vct.value
		
		 and vct.companyid=#{record.companyId}
		
		LEFT JOIN t_sys_user u1 on lc.counsoler = u1.id
		
		 and u1.companyid=#{record.companyId}
		
		LEFT JOIN v_customer_order_status vcos on lc.`status`= vcos.`value`
		LEFT JOIN (select GROUP_CONCAT(tp.project_name order by tca.createTime SEPARATOR '/') as projectnames,tca.customer_id from t_ltn_customer_consumpton_amount tca LEFT JOIN t_project tp on tca.project_id=tp.id GROUP BY tca.customer_id) tcdt on tcdt.customer_id=lc.id
		where 1=1
		
			and lc.company_id=#{record.companyId}
 		
 		
			and lc.phonenumb like concat(#{record.phonenumb},'%')
 		
 		
			and lc.customername like concat(#{record.customername},'%')
 		
 		
			and lc.therapeutist = #{record.therapeutist}
 		
 		
			and lc.chu_fu_zhen = #{record.chuFuZhen}
 		
 		
			and lc.status = #{record.status}
 		
 		
			and lc.id = #{record.id}
 		
 		
			and lc.createtime >= #{record.createtime}
 		
 		
			and lc.createtime <= #{record.endtime}
 		
 		GROUP BY lc.id,lc.company_id,vct.text,u2.nickname,tcdt.projectnames,u3.nickname,vcos.text
		order by lc.updateTime desc
