mapper.select:
SELECT p.id,p.`name`,p.message,p.title,p.type,p.`status`,p.company_id as companyId,p.create_time,p.create_user,p.update_time,p.update_user,vs.`name` as statusName,vmt.text as typeName,tc.`name` as companyName FROM `t_message` p LEFT JOIN v_vip_customer_status vs on p.`status`=vs.`value` and p.company_id=vs.companyid LEFT JOIN v_message_type vmt on p.type= vmt.`value` LEFT JOIN t_company tc on p.company_id = tc.id where 1=1
and p.name like concat('%',#{name},'%')
and p.status=#{status}
and p.type=#{type}
order by p.update_time desc
limit #{index},#{pageSize}

select count(*) from( SELECT p.id,p.`name`,p.message,p.title,p.type,p.`status`,p.company_id as companyId,p.create_time,p.create_user,p.update_time,p.update_user,vs.`name` as statusName,vmt.text as typeName,tc.`name` as companyName FROM `t_message` p LEFT JOIN v_vip_customer_status vs on p.`status`=vs.`value` and p.company_id=vs.companyid LEFT JOIN v_message_type vmt on p.type= vmt.`value` LEFT JOIN t_company tc on p.company_id = tc.id where 1=1
and p.name like concat('%',#{name},'%')
and p.status=#{status}
and p.type=#{type}
) temp
