mapper.select:
SELECT p.id,p.company_id as companyId,p.project_nums as projectNums,p.discount,p.money,p.image_url imageUrl,p.project_name projectName,p.remark,p.`status`,p.createuser,tc.`name` as companyName,p.createtime,p.project_type as projectType,vpt.`name` as projectTypeName,p.deadline FROM `t_project` p LEFT JOIN t_company tc on p.company_id=tc.id LEFT JOIN v_project_type vpt on p.project_type=vpt.`value` and p.company_id=vpt.companyid where 1=1
and p.project_name like concat('%',#{projectname},'%')
and p.status=#{status}
and p.discount=#{discount}
and p.company_id=#{companyid}
and p.remark like concat('%',#{remark},'%')
order by p.updatetime desc
limit #{index},#{pageSize}

select count(*) from( SELECT p.id,p.company_id as companyId,p.project_nums as projectNums,p.discount,p.money,p.image_url imageUrl,p.project_name projectName,p.remark,p.`status`,p.createuser,tc.`name` as companyName,p.createtime,p.project_type as projectType,vpt.`name` as projectTypeName,p.deadline FROM `t_project` p LEFT JOIN t_company tc on p.company_id=tc.id LEFT JOIN v_project_type vpt on p.project_type=vpt.`value` and p.company_id=vpt.companyid where 1=1
and p.project_name like concat('%',#{projectname},'%')
and p.status=#{status}
and p.discount=#{discount}
and p.company_id=#{companyid}
and p.remark like concat('%',#{remark},'%')
) temp
