select.if:
		SELECT tsm.id,tsm.text,tsm.`code`,tsm.`view`,tsm.jsp,tsm.state,tsm.`status`,tsm.companyid,tsm.`view` as iconCls,tsm.parendId from t_sys_menu tsm INNER JOIN t_sys_role_menu tsrm on tsm.id = tsrm.menuid 
		where 1=1 
 			and tsm.status=1
		
 			and tsm.parendId=#{pid}
 		
 		
 			and tsrm.roleid in 
 			
                        #{item}
            
 		
 		
 			and tsm.companyid = #{companyid}
 		
 		GROUP BY tsm.id,tsrm.companyid
 		ORDER BY tsm.code asc

		SELECT a.id,a.text,a.`code`,a.jsp,a.state,a.`status`,a.`view` as iconCls,a.companyid,a.parendId,GROUP_CONCAT(b.roleid) as roleids,
			if((
				SELECT count(1) from t_sys_role_menu c 
					INNER JOIN t_sys_menu m on c.menuid=m.id 
					where 1=1 
					
			 			and c.roleid=#{roleid,jdbcType=INTEGER}
			 		
			 		
			 			and m.parendId=#{pid,jdbcType=INTEGER}
			 		
			 		
		 				and m.companyid = #{companyid}
		 			
					 and c.menuid=a.id
				)>0,true,false) checked 
		FROM `t_sys_menu` a
		INNER JOIN t_sys_role_menu b on a.id = b.menuid
		where 1=1
		
 			and a.parendId=#{pid,jdbcType=INTEGER}
 		
 		
 			and a.companyid = #{companyid}
 		
		GROUP BY a.id
 		ORDER BY a.code asc

mapper.select:
SELECT a.id,a.text,a.`code`,a.jsp,a.state,a.`status`,a.companyid,a.`view` as iconCls,a.parendId,
	if((
		SELECT count(1) from t_sys_role_menu c 
			INNER JOIN t_sys_menu m on c.menuid=m.id 
			where 1=1 and m.status=1
			
	 			and c.roleid=#{roleid,jdbcType=INTEGER}
	 		
	 		
	 			and m.companyid = #{companyid}
	 		
			 and c.menuid=a.id
		)>0,true,false) checked
FROM `t_sys_menu` a
where 1=1 and a.status=1

	and a.companyid = #{companyid}

GROUP BY a.id
	ORDER BY a.code asc
