mapper.select:
select 

from t_sys_timer_schedule_workflow
where schedule_id = #{scheduleId,jdbcType=INTEGER}
  and workflow_id = #{workflowId,jdbcType=INTEGER}

mapper.delete:
delete from t_sys_timer_schedule_workflow
where schedule_id = #{scheduleId,jdbcType=INTEGER}
  and workflow_id = #{workflowId,jdbcType=INTEGER}

mapper.select:
select ts.`name` as schedulename,tsw.schedule_id as scheduleId ,tsw.workflow_id as workflowId,tws.name,tsw.`status`,tsw.ignoreError,tsw.replay,tsw.replaySize,tsw.remark
from t_sys_timer_schedule_workflow tsw LEFT JOIN t_sys_timer_scheduleconfig ts on tsw.schedule_id=ts.id
where 1=1 and tsw.schedule_id=#{scheduleId} and tsw.workflow_id=#{workflowId}
