SELECT vtt1.value,vtt1.text text, IFNULL(temp.day,#{record.day}) day, ifnull(temp.user_id,#{record.userId}) userid,ifnull(temp.nickname,(select a.nickname from t_sys_user a where a.id=#{record.userId})) nickname, if(temp.`status`=1,1,0) status, if(temp.`status`=1 or (temp.`status` is null and CAST(vtt1.`value` AS SIGNED)<DATE_FORMAT(now(),'%H')),true,false) disabled,temp.chuFuZhenName from v_treatment_time vtt1 LEFT JOIN ( SELECT vtt.text,vtt.`value`,a.day,a.user_id,tsu.nickname,a.status,vcfz.text as chuFuZhenName FROM v_treatment_time vtt INNER JOIN `t_therapist_treatment_time_info` a on a.treatment_time = vtt.`value` INNER JOIN t_sys_user tsu on a.user_id=tsu.id left join v_chu_fu_zhen vcfz on a.chu_fu_zhen=vcfz.value where a.`status`=1
and a.`day` = #{record.day}
and a.user_id = #{record.userId} ) temp on vtt1.`value` = temp.value GROUP BY vtt1.value,vtt1.text,temp.DAY,temp.chuFuZhenName order by CAST(vtt1.`value` AS SIGNED)

