mapper.select:
 		SELECT a.id,a.outpath,a.name,a.type,a.filename,a.name_type as nametype,a.input_params as inputParams,a.`status`,a.createtime,a.createuser,a.updatetime,a.updateuser
		,a.groupname,a.remark,b.`code`,b.`name` as inputParamsName,c.name as typename
 		from t_sys_timer_job_data_export a 
 		LEFT JOIN t_sys_data b on a.input_params = b.id 
 		LEFT JOIN v_export_type c on a.type = c.value 
		where 1=1 
		
 				and a.groupname=#{groupname}
	 	
	 	
	 		
				and a.createuser=#{createuser}
	 		
	 	
		order by a.updateTime desc 
		
			limit #{index},#{pageSize}
