mapper.insert:
insert into t_sys_timer_workflow (id, name, status, 
  ignoreError, replay, replaySize, 
  groupName, createTime, updateTime, 
  createUser, remark, updateUser
  )
values (#{id,jdbcType=BIGINT}, #{name,jdbcType=VARCHAR}, #{status,jdbcType=BIT}, 
  #{ignoreerror,jdbcType=BIT}, #{replay,jdbcType=BIT}, #{replaysize,jdbcType=INTEGER}, 
  #{groupname,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}, #{updatetime,jdbcType=TIMESTAMP}, 
  #{createuser,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}, #{updateuser,jdbcType=VARCHAR}
  )

mapper.select:
 		select concat(wf.name,'-',ifnull(wf.createUser,'')) as text,'open' as state,wf.* from t_sys_timer_workflow wf
 		where 1=1 
 		
 			and wf.groupname=#{groupname}
 		
 		
 			
				and wf.createuser=#{createuser}

select.if:
   		select job.id as value ,job.name,1 as type from t_sys_timer_job_sql_query job where job.`status`=1
   		
 				and job.groupname=#{groupname}
	 	
	 	
	 		
				and job.createuser=#{createuser}
	 		
	 	
		union 
		select job.id as value ,job.name ,2 as type from t_sys_timer_job_elasticSearch job where job.`status`=1
		
 				and job.groupname=#{groupname}
	 	
	 	
	 		
				and job.createuser=#{createuser}
	 		
	 	
		union 
		select job.id as value ,job.name ,3 as type from t_sys_timer_job_exeShell job where job.`status`=1
		
 				and job.groupname=#{groupname}
	 	
	 	
	 		
				and job.createuser=#{createuser}
	 		
	 	
		union 
		select job.id as value ,job.name ,4 as type from t_sys_timer_job_hive job where job.`status`=1
		
 				and job.groupname=#{groupname}
	 	
	 	
	 		
				and job.createuser=#{createuser}
	 		
	 	
		union 
		select job.id as value ,job.name ,5 as type from t_sys_timer_job_sqoop job where job.`status`=1
		
 				and job.groupname=#{groupname}
	 	
	 	
	 		
				and job.createuser=#{createuser}
	 		
	 	
		union 
		select job.id as value ,job.name ,6 as type from t_sys_timer_job_sftp job where job.`status`=1
		
 				and job.groupname=#{groupname}
	 	
	 	
	 		
				and job.createuser=#{createuser}
	 		
	 	
		union 
		select job.id as value ,job.name ,7 as type from t_sys_timer_job_sql_insert_update job where job.`status`=1
		
 				and job.groupname=#{groupname}
	 	
	 	
	 		
				and job.createuser=#{createuser}
	 		
	 	
		union 
		select job.id as value ,job.name ,8 as type from t_sys_timer_job_sql job where job.`status`=1
		
 				and job.groupname=#{groupname}
	 	
	 	
	 		
				and job.createuser=#{createuser}
	 		
	 	
	 	union 
		select job.id as value ,job.name,9 as type from t_sys_timer_job_data_export job where job.`status`=1
		
 				and job.groupname=#{groupname}
	 	
	 	
	 		
				and job.createuser=#{createuser}
	 		
	 	
	 	union 
		select job.id as value ,job.name,10 as type from t_sys_timer_job_data_import job where job.`status`=1
		
 				and job.groupname=#{groupname}
	 	
	 	
	 		
				and job.createuser=#{createuser}
	 		
	 	
	 	union 
		select job.id as value ,job.name,11 as type from t_sys_timer_job_mongo_query job where job.`status`=1
		
 				and job.groupname=#{groupname}
	 	
	 	
	 		
				and job.createuser=#{createuser}
	 		
	 	
		ORDER BY type ,value
